class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans =abs(target[0] - nums[0])*1ll;
        int n = nums.size();
        cout<<ans<<endl;
        for(int i =1;i<n;i++)
        {
            if(nums[i]<=target[i] and nums[i-1]<=target[i-1])
            {
                ans += max(0, (target[i]-nums[i] )- (target[i-1] -nums[i-1]));
            }
            else if(nums[i]<=target[i] and nums[i-1]>=target[i-1])
            {
                ans += (target[i] - nums[i]);
            }
            else if(nums[i]>=target[i] and nums[i-1]>=target[i-1])
            {
                 ans += max(0, (nums[i]-target[i] )- (nums[i-1] -target[i-1]));
            }
            else if(nums[i]>=target[i] and nums[i-1]<=target[i-1])
            {
                ans+= (nums[i] - target[i]);
            }
        }


        return ans ;

    }
};
