class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        arr1, arr2 = [], []
        for i, (a, b) in enumerate(zip(nums, target)):
            diff, arr = (a-b, arr1) if a>b else  (b-a, arr2)
            if diff:
                if arr and arr[-1][2]+1==i:
                    arr[-1][0].append(diff)
                    arr[-1][2] = i
                else:
                    arr.append([[diff], i, i])
        # print(arr1)
        # print(arr2)
        # print([a-b for i, (a, b) in enumerate(zip(nums, target))])

        def solve(arr):
            res = 0

            for values, _, _ in arr:
                stack = []
                for value in values:
                    diff = 0
                    while stack and stack[-1]>=value:
                        diff = max(diff, stack.pop()-value)
                    # print(value, diff)
                    res+=diff
                    stack.append(value)
                if stack:
                    res+=stack.pop()
                # print(res, stack)
            return res
        return solve(arr1)+solve(arr2)
