const int N=1e5+10;
class Solution {
    typedef long long LL;
    int a[N],b[N];
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for (int i = 1; i <= n; i ++ ) a[i]=target[i-1]-nums[i-1];
        for (int i = 1; i <= n; i ++ ) b[i] = a[i] - a[i - 1];
        // for(int i=1;i<=n;i++)cout<<a[i]<<" "<<b[i]<<'\n';

        LL p = 0, q = 0;
        for (int i = 1; i <= n; i ++ )
            if (b[i] > 0) p += b[i];
            else q -= b[i];

        return max(p,q);

    }
