class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] diffs = new int[n];
        for(int i =  0; i< n; i++){
            diffs[i] = target[i] - nums[i];
        }
        // Arrays.sort(diffs);
        return minOperations(diffs);

    }

    static long minOperations(int[] arr) {
        int n = arr.length;
        if (n == 0) {
            return 0;
        }
        long operations = 0;
        int zeros = 0;
        for(int i:arr) if(i==0) zeros++;
        int i =  0;
        while(zeros<n){
            if(i==n) i=0;
            if(arr[i]==0){
                i++;
                continue;
            }
            int l = i;
            boolean sign = arr[i]<0;
            int r = l;
            int limit = arr[i];
            while((r < n) && ((sign && arr[r]<0) || (!sign && arr[r]>0))){
                if(sign) limit = Math.max(limit,arr[r]);
                else limit = Math.min(limit,arr[r]);
                r++;
            }
            operations += Math.abs(limit);
            // System.out.println(l+" "+(r-1)+" "+sign + " " + limit);
            for(int j = l;j<r;j++){
                // System.out.println(j+" "+arr[j]);
                arr[j] -= limit;
                // System.out.println(j+" "+arr[j]);
                if(arr[j]==0) zeros++;
            }
        }


        return operations;

    }
}
