func minimumOperations(nums []int, target []int) int64 {
    n := len(nums)
    diff := make([]int, n)
    dd := make([]int, n)

    for i, v := range nums {
        diff[i] = v - target[i]
    }

    dd[0] = diff[0]
    for i:=1; i<n; i++ {
        dd[i] = diff[i]-diff[i-1]
    }

    fmt.Println(diff, dd)

    f := 0
    z := 0
    for i:=0; i<n; i++ {
        if dd[i] > 0 {
            z += dd[i]
        } else {
            f += -dd[i]
        }
    }

    return int64(max(z, f))
}
