class Solution {
public:
    long long getSubOps(vector<int>&v){
        int sz=v.size();
        if(v[0]<0){
            for(int i=0;i<sz;i++) v[i]=abs(v[i]);
        }
        long long prevOp=0;
        long long res=0;
        for(int i=0;i<sz;i++){
            if(v[i]>=prevOp){
                res+=(v[i]-prevOp);
                prevOp=v[i];
            }
            else{
                prevOp=v[i];
            }
        }
        return res;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int>diff(n);
        for(int i=0;i<n;i++) diff[i] = target[i]-nums[i];
        int i=0;
        long long ans=0;
        while(i<n){
            int j=i;
            vector<int>v;
            if(diff[j]>=0){
                while(j<n && diff[j]>=0){
                    v.push_back(diff[j]);
                    j++;
                }
            }
            else{
                while(j<n && diff[j]<0){
                    v.push_back(diff[j]);
                    j++;
                }
            }
            ans+=getSubOps(v);
            i=j;
        }
        return ans;
    }
};
