class Solution {
public:

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int ct = 0;
        int a = 0, b = 0;
        if(nums[0]>=target[0]){
           ct = 1;
           a = nums[0]-target[0];
        }
        else{
            b = target[0]-nums[0];
        }
        ans = ans+abs(nums[0]-target[0]);
        for(int i = 1; i<nums.size(); i++){
           if(nums[i]>=target[i]){
             if(ct==1){
              if(nums[i]-target[i]<=a){
                 a = nums[i]-target[i];
              }
              else{
                 ans = ans+nums[i]-target[i]-a;
                 a = nums[i]-target[i];
              }
             }
             else{
                ans = ans+nums[i]-target[i];
                ct = 1;
                a = nums[i]-target[i];
             }
           }
           else{
               if(ct==0){
                  if(target[i]-nums[i]<=b){
                    b = target[i]-nums[i];
                  }
                  else{
                    ans = ans+target[i]-nums[i]-b;
                    b = target[i]-nums[i];
                  }
               }
               else{
                ans = ans+target[i]-nums[i];
                ct = 0;
                b = target[i]-nums[i];
               }
           }
        }
        return ans;
    }
};
