class Solution {
public:
long long minimumOperations(vector<int>& nums, vector<int>& target) {
    int n = nums.size();
    vector<int> v;

    int positive = 0;
    int negative = 0;
    int zeros = 0;
    for(int i = 0; i < n; i++) {
        int diff = target[i] - nums[i];
        v.push_back(diff);
        if(diff == 0) zeros++;
        else if(diff > 0) positive++;
        else negative++;
    }

    long long total_operations = 0;
    int current_segment_diff = 0;

    if(negative == 0 || positive == 0) {
        // all same sign
        for(int i = 0; i < n; ++i) {
            if(i == 0 || (v[i - 1] > 0 && v[i] > 0) || (v[i - 1] < 0 && v[i] < 0)) {
                int additional_operations = abs(v[i]) - abs(current_segment_diff);
                if(additional_operations > 0) {
                    total_operations += additional_operations;
                }
                current_segment_diff = v[i];
            } else {
                total_operations += abs(v[i]);
                current_segment_diff = v[i];
            }
        }
    } else {
        // diff sign
        for(int i = 0; i < n; ++i) {
            if(i == 0 || (v[i - 1] > 0 && v[i] > 0) || (v[i - 1] < 0 && v[i] < 0)) {
                int additional_operations = abs(v[i]) - abs(current_segment_diff);
                if(additional_operations > 0) {
                    total_operations += additional_operations;
                }
                current_segment_diff = v[i];
            } else {
                total_operations += abs(v[i]);
                current_segment_diff = v[i];
            }
        }
    }

    return total_operations;
}


};
