class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& v) {
        int n=nums.size();
        long long curr=nums[0]-v[0];
        long long ans=abs(nums[0]-v[0]);
        for(int i=1;i<n;i++){
            int diff=nums[i]-v[i];
            if(curr>0 && diff<=0){
                ans+=abs(nums[i]-v[i]);
            }
            else if(curr<0 && diff>=0){
                ans=ans+abs(nums[i]-v[i]);
            }
            else{
              if(abs(curr)<abs(diff)){
                int x=curr-diff;
                  ans=ans+abs(x);
              }
            }
            curr=diff;
        }
        return ans;
    }
};
