class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ans = 0
        diff = []
        n = len(nums)
        for i in range(n):
            diff.append(target[i]-nums[i])
        mn, mx = 0, 0
        for x in diff:
            if x >= 0:
                if mx >= x:
                    val = mx-x
                    mx = max(0, mx-val)
                    mn = 0
                else:
                    ans += (x-mx)
                    mx += (x-mx)
                    mn = 0
            else:
                if mn < x:
                    val = x - mn
                    mn = min(0, mn+val)
                    mx = 0
                else:
                    val = mn - x
                    ans += val
                    mn -= val
                    mx = 0
        return ans
