class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        long long count = 0;
        int size = nums.size();
        long last = 0;
        long diff;
        for(int i=0; i<size; i++)
        {   
            diff = target[i] - nums[i];
            //std::cout << "diff " << diff << " last " << last << std::endl;
            if(last > 0 && diff > 0 && diff<last)
                count += (last - diff);

            if(last > 0 && diff <= 0 && diff<last)
                count += last;

            if(last < 0 && diff < 0 && diff>last)
                count -= (last - diff);

            if(last < 0 && diff >= 0 && diff>last)
                count -= (last);

            last = diff;
        }

        count += abs(last);


        return count;
    }
};

