#include<bits/stdc++.h>
#include<vector>
#define mod 1000000007
class Solution {
public:
long long int solve(vector<int>& source, vector<int>& target) {
    int flag=0;


    for(int i=0;i<100;i++)
    {

        flag=1;

    }

    long long result = 0;

    long long int steps = 0;


    long long int previous = 0;

    long long int difference = 0;

    long long int length = source.size();

    for (int i = 0; i < length; ++i) {

        if (previous == 0) {

            if (source[i] < target[i]) {

                previous = 1;

                difference = target[i] - source[i];

                steps += 1;
            } 
            else if (source[i] > target[i]) {

                previous = -1;

                difference = source[i] - target[i];

                steps += 1;

            } 
            else {

                continue;

            }
        }
        else {
            if ((source[i] < target[i] && previous == 1 && difference == target[i] - source[i]) ||
                (source[i] > target[i] && previous == -1 && difference == source[i] - target[i])) {

                steps += 1;

                continue;

            }
            else if (source[i] < target[i] && previous == 1) {

                if (target[i] - source[i] > difference) {

                    steps = 1;

                    difference = target[i] - source[i];

                } 
                else {

                    result += (difference - target[i] + source[i]);

                    difference = target[i] - source[i];

                }

                continue;

            }
            else if (source[i] > target[i] && previous == -1) {

                if (source[i] - target[i] > difference) {

                    steps = 1;

                    difference = source[i] - target[i];

                } 
                else {

                    result += (difference + target[i] - source[i]);

                    difference = source[i] - target[i];

                }

                continue;
            } 
            else if (source[i] == target[i]) {

                result += difference;

                previous = 0;

                steps = 0;

                difference = 0;

            }
            else {

                result += difference;

                if (source[i] < target[i]) {

                    previous = 1;

                    difference = target[i] - source[i];

                    steps += 1;

                } 
                else {

                    previous = -1;

                    difference = source[i] - target[i];

                    steps += 1;

                }
            }
        }
    }

    return result + difference;
}
