class Solution:
    def cnt(self, nums:List[int]) -> int:
        if (nums[0] == 0): return 0
        if (nums[0] < 0):
            nums = [-num for num in nums]
        ans = nums[0]
        for i in range(1, len(nums)):
            if (nums[i] > nums[i - 1]):
                ans += nums[i] - nums[i - 1]
        return ans

    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        arr = [target[i] - nums[i] for i in range(len(nums))]
        l, r = 0, 0
        ans = 0
        while (l < len(arr)):
            r = l + 1
            while (r < len(arr) and arr[l] * arr[r] > 0): r += 1
            ans += self.cnt(arr[l:r])
            l = r
        return ans
