class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=abs(target[0]-nums[0]);
        long long prev=nums[0]-target[0];// +ve means decrement, -ve means increment
        int n=nums.size();
        // cout<<"ans="<<ans<<endl;
        for(int i=1;i<n;++i){
            long long cur=nums[i]-target[i];
            // cout<<cur<<' '<<prev<<endl;
            if(cur==0){prev=0;continue;}
            if(prev==cur)continue;
            if(prev==0)ans+=abs(cur);
            // if decrement 
            else if(prev<0 && cur<0){
                if(cur<prev){//dec more
                    ans+=abs(abs(prev)-abs(cur));
                }
            }else if(prev<0 and cur>0)ans+=abs(cur);
            else if(prev>0 and cur<0)ans+=abs(cur);
            else{
                //prev > 0 and cur > 0
                if(cur>prev)ans+=abs(prev-cur); 
            }
            // cout<<"i = "<<i<<endl;
            // cout<<"ans = "<<ans<<endl;
            prev=nums[i]-target[i];
        }
        return ans;
    }
};
