class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        using LL = long long;
        int n = nums.size();
        int a[100010];
        for (int i = 0; i < n; i ++ ) {
            a[i] = nums[i] - target[i];
        }
        LL mn = 0;
        int num = 0;
        for (int i = 0; i < n; i ++ ) {
            if ((num < 0 && a[i] < 0) || (num > 0 && a[i] > 0)) {
                if (abs(num) < abs(a[i])) {
                    mn += abs(a[i] - num);
                }
                num = a[i];
            } 
            else {
                mn += abs(a[i]);
                num = a[i];
            }
        }
        return mn;
    }
};
