class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int prev = 0;

        long ans = 0;
        for(int i = 0 ;  i < nums.length ; i++) {
            int j = target[i] - nums[i];

            if( j > 0) {
                if(prev >= 0) {
                    if(prev > j) {

                    } else {
                        ans += j - prev;
                    }
                } else {
                    ans += Math.abs(j);
                }
            } else {
                if(prev <= 0) {
                    if(prev < j) {

                    } else {
                        ans += prev - j;
                    }
                } else {
                    ans += Math.abs(j);
                }
            }

            prev = j;
        }

        return ans;
    }
}
