class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        std::vector<int> diff;
        for (int i=0; i<nums.size(); i++) {
            diff.push_back(target[i]-nums[i]);
        }

        // print diff
        // std::cout << "[";
        // for (int i=0; i<diff.size(); i++) {
        //     std::cout << diff[i];
        //     if (i<diff.size()-1) std::cout << ", ";
        // }
        // std::cout << "]\n";


        // 
        for (int i=0; i<nums.size(); i++) {
            // std::cout << "#### diff["<<i<<"]: " << diff[i] << " ####\n";
            if (i>0 && same_sign(diff[i], diff[i-1])) {
                if (abs(diff[i])<=abs(diff[i-1])) {
                    // std::cout << "same sign <= prev, continue\n";
                    continue;
                }
                else {
                    // std::cout << "same sign > prev, add\n";
                    res += abs(diff[i]-diff[i-1]);
                }
            } else {
                // std::cout << "cannot carry on\n";
                res += abs(diff[i]);
            }
            // std::cout << "res=" << res << "\n";
        }
        return res;
    }

    bool same_sign(int a, int b) {
        return ((a>0&&b>0) || (a<0&&b<0));
    }
};
