class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        count = 0
        ans = 0
        for i in range(len(nums)):
            if nums[i] == target[i]: 
                count = 0
            elif nums[i] > target[i]:
                count = min(0, count)
                diff = target[i] - nums[i]
                ans += abs(count - diff) if diff < count else 0
                count = diff
            else:
                count = max(0, count)
                diff = target[i] - nums[i]
                ans += abs(count - diff) if count < diff else 0
                count = diff
        return ans
