class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> v(n);
        for(int i=0;i<nums.size();i++){
           v[i] = target[i]-nums[i];
        }
        ans+=abs(v[0]);
        int last = abs(v[0]);
        bool parity = 0;
        if(v[0]>=0){
            parity = 1;
        }

        for(int i=1;i<n;i++){
            if(v[i]>=0 && parity==0){
                parity = 1;
                last = abs(v[i]);
                ans += last;
            }else if(v[i]<0 && parity==1){
                parity = 0;
                last = abs(v[i]);
                ans+=last;
            }else if(last>=abs(v[i])){
                last = abs(v[i]);
            }else if(last<abs(v[i])){
                ans+=abs(v[i])-last;
                last = abs(v[i]);
            }
        }
        return ans;
    }
};
