typedef long long ll;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        ll ans = 0;
        const ll ZERO = 0;

        ll n = nums.size();

        pair<ll,ll> pos_carry( 0, 0); // cost, carry
        pair<ll,ll> neg_carry( 0, 0); // cost, carry

        for( ll i=0; i<n; i++) {

            ll v = nums[i];
            ll t = target[i];
            ll p = t-v;

            if(v<t) {

                ll d = max( t-(v+pos_carry.second), ZERO);

                pos_carry.first = min( pos_carry.first + d, neg_carry.first + t - v);
                pos_carry.second = abs(p);

                neg_carry.first += abs(t-v)+neg_carry.second;

            } else {

                ll d = max((v-neg_carry.second)-t, ZERO);

                neg_carry.first = min( neg_carry.first + d, pos_carry.first + v - t);
                neg_carry.second = abs(p);

                pos_carry.first += abs(t-v)+pos_carry.second;
            }

            // cout << " p cost: " <<  pos_carry.first << " carry: " << pos_carry.second << endl;
            // cout << " n cost: " <<  neg_carry.first << " carry: " << neg_carry.second << endl;
            // cout << endl;
        }

        ans = min( pos_carry.first, neg_carry.first);

        return ans;
    }
};
