class Solution:
    def c(self, l, t = 0):
        if not l:
            return 0
        cm = 1e10
        lm = []
        for i, n in enumerate(l):
            if n < cm:
                cm = n
                lm = [-1, i]
            elif n == cm:
                lm.append(i)
        lm.append(len(l))
        r = cm-t
        for a, b in pairwise(lm):
            r += self.c(l[a+1:b], cm)
        return r

    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        l = []
        for n, t in zip(nums, target):
            l.append(n-t)
        i = 0
        r = 0
        while i < len(nums):
            if l[i] == 0:
                i += 1
                continue
            la = l[i]
            c = []
            while i < len(nums) and la * l[i] > 0:
                c.append(abs(l[i]))
                i += 1
            r += self.c(c)

        return r
