class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int len =  nums.size();
        vector<int> diff(len, 0);
        for(int i=0;i<len;i++){
            diff[i]=target[i]-nums[i];
        }
        long long ans = abs(diff[0]);
        for(int i=1;i<len;i++){
            //cout<<"hi"<<endl;
            if(diff[i]==0){
                continue;
            } else {
                //cout<<"hey"<<endl;
                if((diff[i]<0 && diff[i-1]<0) || (diff[i]>0 && diff[i-1]>0)){ // same streak
                    if(abs(diff[i-1])>=abs(diff[i])){ //already done
                        continue;
                    } else{
                        ans = ans + abs(diff[i])-abs(diff[i-1]);
                    }
                } else { // different streak
                    ans = ans + abs(diff[i]);
                }
            }
        }
        return ans;
    }
};
