class Solution{
public:
    long long minimumOperations(vector<int>& B, vector<int>& C){

        int n=B.size();
        vector<long long> A(n);

        for(int i=0;i<n;i++){
            A[i]=C[i]-B[i];
        }

        long long ans=0;
        long long curr=0;

        for(int i=0;i<n;i++){
            if(i==0 || (A[i-1]<0 && A[i]<0) || (A[i-1]>0 && A[i]>0)){
                long long dif=abs(A[i])-abs(curr);
                if(dif>0){
                    ans+=dif;
                }
                curr=A[i];
            }
            else{
                ans+=abs(A[i]);
                curr=A[i];
            }
            cout<<curr<<' ';
        }

        cout<<'\n';
        return ans;
    }
};
