class Solution {
public:
    long minimumOperations(std::vector<int>& nums, std::vector<int>& target) {
        vector<int> diff(nums.size());
        for (int i = 0; i < nums.size(); i++) {
            diff[i] = target[i] - nums[i];
            cout<<diff[i];
        }

        long total = 0;
        int current = 0;



        for (int i = 0; i < diff.size(); ++i) {
            if (i == 0) {
                total += abs(diff[i]);
                current = diff[i];
            } else if ((diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int mx = abs(diff[i])-abs(current);
                if (mx > 0) {
                    total += mx;
                }
                current = diff[i];
            } else {
                total += abs(diff[i]);
                current = diff[i];
            }
        }

        return total;
    }
};
