class Solution {
    public long minimumOperations(int[] nums, int[] target) {
 int n = nums.length;
        int[] temp = new int[n];
        for (int i = 0; i < n; i++) {
            temp[i] = target[i] - nums[i];
        }
        long res = 0;
        int pre = 0;
        for (int i = 0; i < n; i++) {
            if(temp[i] == 0){
                pre = 0;
            }
            if (temp[i] > 0) {
                if (pre < 0) {
                    pre = 0;
                }
                if(temp[i]> pre){
                    res += temp[i] -pre;
                }
                pre = temp[i];
            }
            if(temp[i] < 0){
                if(pre > 0){
                    pre = 0;
                }
                if(temp[i] < pre){
                    res += Math.abs(temp[i]-pre);
                }
                pre = temp[i];
            }

        }
        return res;
    }
}
