using ll=long long;

class Solution {
public:

    ll solve(vector<ll> c) {
        int n=c.size();
        ll lo = *min_element(c.begin(), c.end());
        ll res=lo;
        for (int i=0; i<n; i++) {
            res += max(0ll, c[i]-(i-1>=0 ? c[i-1] : lo));
        }
        return res;
    }
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n=a.size();
        vector<ll> c(n);
        for (int i=0; i<n; i++) {
            c[i]=b[i]-a[i];
        }

        // for (ll x: c) cout<<x<<" ";
        // cout<<endl;

        ll res=0;
        for (int i=0; i<n; ) {
            if (c[i]==0) {
                i++; continue;
            }
            int j=i;
            vector<ll> buf;
            while (j<n && c[i]*c[j]>0) {
                buf.push_back(abs(c[j]));
                j++;
            }
            ll cur=solve(buf);
            // cout<<"cur: "<<cur<<endl;
            res += cur;
            i=j;
        }
        return res;

    }
};
