using ll=long long;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n=nums.size(),TO = 0,csegDF = 0;       
        vector<ll> df(n);
        for(ll i=0;i<n;i++){
            df[i]=target[i]-nums[i];
        }
        for(ll i=0;i<n;i++){
            if ( (i == 0 || (df[i - 1] > 0 and df[i] > 0)) || (df[i - 1] < 0 and df[i] < 0)){
                ll addOP = abs(df[i]) - abs(csegDF);
                if(addOP>0)TO += addOP;
                csegDF = df[i];
            }
            else{
                TO += abs(df[i]);
                csegDF = df[i];     
            }
        }
        return TO;   
    }
};
