class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        if(nums==null||target==null){
            return 0;
        }
        int[] posdiff=new int[nums.length];
        int[] negdiff=new int[nums.length];
        for(int i=0;i<nums.length;i++){
            int x=target[i]-nums[i];
            if(x>0){
                posdiff[i]=x;
            }
            else{
                negdiff[i]=-x;
            }
        }
        return minOp(posdiff)+minOp(negdiff);
    }
    public long minOp(int[] target) {
        long result=0;
        int j=0;
        for(int i:target){
            if(i>j){
                result+=i-j;
            }
            j=i; 
        }

        return result;
    }
}
