class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [a-b for a,b in zip(nums, target)]
        print(diff)
        res = 0
        index = 0


        while index < len(diff):
            if diff[index] == 0:
                index += 1
                continue
            elif diff[index] < 0:
                stack = [(diff[index], -diff[index])]
                count = 0
                index += 1
                while index < len(diff) and diff[index] < 0:
                    while stack and stack[-1][0] < diff[index]:
                        val, prev = stack.pop(-1)
                        count += min(prev, diff[index] - val)
                    if not stack:
                        stack.append((diff[index], 0 - diff[index]))
                    else:
                        stack.append((diff[index], 0 - (diff[index]-stack[-1][0])))

                    index += 1
                while stack:
                    count += stack[0][1]
                    stack.pop(0)
                res += count
            else:
                stack = [(diff[index], diff[index])]
                count = 0
                index += 1
                while index < len(diff) and diff[index] > 0:
                    start = index
