class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        int temp1 = 0; // for positive 
        int temp2 = 0; // for negative
        long long ans = 0;
        for(int i = 0 ; i < n ; i++ )
        {
            int val = (target[i]-nums[i]);
            if(val > 0 )
            {
               temp2 = 0;
               if(val > temp1)
               {
                  ans += (val-temp1);
                  temp1 = val;
               }
                else
                {
                  temp1 = min(temp1,val);
                }
            }
            else if(val < 0)
            {
               temp1 = 0;
               if(val < temp2)
               {
                  ans += (temp2-val);
                  temp2 = val;
               }
                else
                {
                   temp2 = max(temp2,val);
                }
            }
            else
            {
               temp1 = 0;
               temp2 = 0;
            }
        }
        return ans;

