template<typename T> using ordset = tree<T, null_type, less_equal<T>, rb_tree_tag,
                           tree_order_statistics_node_update> ;

// *s.find_by_order(k) -> kth largest element
// s.order_of_key( val )-> Numbers stricly smaller than val
template<typename T >
void myerase(ordset<T> & t, T v) {
    int rank = t.order_of_key(v);//Number of elements that are less than v in t
    auto   it = t.find_by_order(rank); //Iterator that points to the (rank+1)th element in t
    t.erase(it);
}

