class Solution {
public:
    ll go( vector<int> & A )
    {
        int n = si(A) ;
        multiset< pair< int , int> > s ;
        ll r = 0 ;

        vector<int> nxt( n ) , pre( n ) ;

        for ( int i = 0 ; i < n ; i++ ) nxt[i] = i + 1 ; nxt[n-1] = -1 ;
        for ( int i = n - 1 ; i >= 0 ; i-- )pre[i] = i - 1 ; pre[0] = -1 ;

        for ( int i = 0 ; i < n ; i++ )
        {
            if ( A[i] > 0 ) s.ins({ A[i] , i }) ;
        }

        while ( si( s ) )
        {
            auto it = *s.rbegin() ; s.erase( it ) ;

            int ind = it.ss , val = it.ff ;

            int mx = 0 , p = pre[ind] , nx = nxt[ind] ;

            if ( p != -1 ) mx = max( mx , A[p]);
            if ( nx != -1 ) mx = max( mx , A[nx]) ;

            r += 0LL + val - mx ; A[ind] = 0 ;

            if ( p != -1 ) nxt[p] = nx ;
            if ( nx != -1 ) pre[nx] = p ;
        }

        return r ;


    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size() ;
        vector< int > pos( n ) , neg( n ) ;

        for ( int i = 0 ; i < n ; i++ )
        {
            int x = target[i] - nums[i] ;

            if ( x == 0 ) continue ;

            if ( x <  0 ) neg[i] = -x ;
            else pos[i] = x ;
        }
        ll r = go( pos ) + go( neg ) ;
        return r ;
    }
};
