/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {
    const diff = target.map((a, i) => a - nums[i]);

    let res = Math.abs(diff[0]);

    for (let i = 1; i < diff.length; i++) {
        if (diff[i] * diff[i-1] <= 0) {
            res += Math.abs(diff[i]);
        } else {
            if (Math.abs(diff[i]) > Math.abs(diff[i-1])) {
                res += Math.abs(diff[i]) - Math.abs(diff[i-1]);
            }
        }
    }
    return res;
};
