class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        for(int i = 0; i < nums.size(); i++){
            diff.push_back(target[i] - nums[i]);
        }
        long long operation = 0;
        if(diff[0] != 0)
        operation += abs(diff[0]);
        for(int i = 1; i < diff.size(); i++){

            if(diff[i] == diff[i-1] || diff[i] == 0){
                continue;
            }else{
                if(diff[i] > 0){
                    if(diff[i-1] > 0){
                        if(diff[i-1] > diff[i])continue;
                        if(diff[i-1] < diff[i])operation += diff[i] - diff[i-1];
                    }
                    if(diff[i-1] <= 0){
                        operation += diff[i];
                    }
                }
                if(diff[i] < 0){
                    if(diff[i-1] >= 0){
                        operation += abs(diff[i]);
                    }
                    if(diff[i-1] < 0){
                        if(diff[i-1] > diff[i])operation += abs(diff[i] - diff[i-1]);
                        else continue;
                    }
                }
            }
        }
        return operation;
    }
};
