class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        bool check;
        int temp=target[0]-nums[0];
        if(temp>=0) check=true;
        else check=false;
        int diff=0;
        for(int i=0;i<target.size();i++) {

            if(check==true && (target[i]-nums[i])>=0) {
                if(diff<(target[i]-nums[i])) {
                    ans+=(target[i]-nums[i])-diff;
                    diff=target[i]-nums[i];
                }
                else if(diff>(target[i]-nums[i])) {
                    diff=target[i]-nums[i];
                }
            }
            else if(check==false && (target[i]-nums[i])<0) {
                if(diff>(target[i]-nums[i])) {
                    ans+=abs((target[i]-nums[i])-diff);
                    diff=target[i]-nums[i];
                }
                else if(diff<(target[i]-nums[i])) {
                    diff=target[i]-nums[i];
                }
            } 
            else if(check==true && target[i]-nums[i]<0) {
                ans+=abs(target[i]-nums[i]);
                diff=target[i]-nums[i];
                check=false;
            }
            else if(check==false && target[i]-nums[i]>=0) {
                ans+=target[i]-nums[i];
                diff=target[i]-nums[i];
                check=true;
            }
        }
        return ans;
    }
};
