class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = target.size();
        vector<long long> diff(n, 0);
        for(int i = 0; i < n; i++)
        diff[i] = target[i] - nums[i];
        long long start = abs(diff[0]);
        bool positive = true;
        if( diff[0] < 0 )
        positive = false;
        ans += abs(diff[0]);
        for(int i = 1; i < n; i++){
            if( diff[i] >= 0 ){
                if( positive ){
                    ans += abs(diff[i] - abs(min(diff[i], start)));
                    start = abs(diff[i]);
                }else{
                    positive = true;
                    ans += diff[i];
                    start = diff[i];
                }
            }else{
                if( !positive ){
                    long long d = abs(diff[i]);
                    ans += abs(d - abs(min(d, start)));
                    start = abs(diff[i]);
                }else{
                    positive = false;
                    ans += abs(diff[i]);
                    start = abs(diff[i]);
                }
            }
        }

        return ans;
    }
};
