class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [0] * n
        for i, x in enumerate(nums):
            diff[i] = target[i] - x

        def get_op_count(arr):
            if arr[0] < 0:
                arr = [-v for v in arr]
            res = arr[0]
            for i in range(1, len(arr)):
                res += max(arr[i] - arr[i - 1], 0)
            return res

        ans = 0
        i = 0
        while i < n:
            if diff[i] == 0:
                i += 1
                continue
            start = i
            i += 1
            while i < n and (diff[i] > 0) == (diff[start] > 0):
                i += 1
            ans += get_op_count(diff[start: i])
        return ans
