class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diff(n + 1);
        for(int i = 0; i < n; ++i)
        {
            diff[i] = target[i] - nums[i];
        }

        for(int i = n - 1; i > 0; --i)
        {
            diff[i] -= diff[i - 1];
        }

        long long ans1 = 0;
        long long ans2 = 0;
        for(int i = 0; i < n; ++i)
        {
            if(diff[i] > 0)
            {
                ans1 += diff[i];
            }
            else
            {
                ans2 -= diff[i];
            }
        }

        return max(ans1, ans2);
    }   
};
