class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> dn, dt;
        int n = nums.size();
        for (int i = 1; i < n; i++) {
            dn.push_back(nums[i] - nums[i - 1]);
            dt.push_back(target[i] - target[i - 1]);
        }
        long long ans = 0, inc = 0, dec = 0;
        for (int i = 0; i < n - 1; i++) {
            if (dt[i] - dn[i] > 0) {
                if (dt[i] - dn[i] <= inc) {
                    inc -= dt[i] - dn[i];
                }
                else {
                    ans += dt[i] - dn[i] - inc;
                    dec += dt[i] - dn[i] - inc;
                    inc = 0;
                }
            }
            else if (dt[i] - dn[i] < 0) {
                if (dn[i] - dt[i] <= dec) {
                    dec -= -dt[i] + dn[i];
                }
                else {
                    ans += dn[i] - dt[i] - dec;
                    inc += dn[i] - dt[i] - dec;
                    dec = 0;
                }
            }
        }
        // cout << inc << " " << dec << endl;
        if (target[0] - nums[0] >= 0) {
            ans += target[0] - nums[0] >= inc ? target[0] - nums[0] - inc:0;
        }
        else {
            ans += nums[0] - target[0] >= dec ? nums[0] - target[0] - dec:0;
        }

        return ans;
    }
};


