#define ll long long int
#define mod 1000000007

class Solution{
public:
    long long minimumOperations(vector<int> &nums, vector<int> &t){
        int n=nums.size();

        ll res=0;
        int temp=0;
        for(int i=0; i<n; i++){
           if(i==0 || ((t[i]-nums[i])<0 && (t[i-1]-nums[i-1])<0) || ((t[i]-nums[i])>0 && (t[i-1]-nums[i-1])>0)){
                int op=abs(t[i]-nums[i])-abs(temp);
                if(op>0){
                    res += op;
                }
                temp=t[i]-nums[i];
            }
            else{
                res += abs(t[i]-nums[i]);
                temp=t[i]-nums[i];
            }
        }

        return res;
    }
};
