class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int n=nums.size();
        long long ans=0,currdiff=0;
        for(int i=0;i<n;i++)
        {
            if(nums[i]==target[i])
            {
                currdiff=0;
            }
            else {
                int reqdiff=nums[i]-target[i];
                if(currdiff==0||(currdiff>0&&reqdiff<0)||(currdiff<0&&reqdiff>0)) {
                    ans+=abs(reqdiff);
                    currdiff=reqdiff;
                    continue;
                }
                else if(reqdiff==currdiff||abs(reqdiff)<abs(currdiff))
                {
                    currdiff=reqdiff;
                    continue;
                }
                else
                {

                    ans+=abs(reqdiff-currdiff);
                    currdiff=reqdiff;
                }
            }
        }
        return ans;

    }
};
