class Solution {
    func minimumOperations(_ nums: [Int], _ target: [Int]) -> Int {
        let n = nums.count
        var a:[Int] = [0]
        for i in 0..<n{
            a.append(nums[i]-target[i])
        }

        var ans = 0
        var s1 = 0
        var s2 = 0
        for i in (1...n).reversed(){
            a[i] = a[i]-a[i-1]
            if a[i] < 0 {
                s1 += -a[i]
            }else{
                s2 += a[i]
            }
        }

        ans = max(s1,s2)

        return ans
    }
}
