class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        curr = 0
        op = 0 

        for i , num in enumerate(nums): 
            diff = num - target[i]

            if (curr > 0 and diff > 0) or (curr < 0 and diff < 0):

                if abs(curr) < abs(diff):
                    op += abs(curr - diff)

            else:
                op += abs(diff)
            curr = diff 

        return op
