class Solution {
public:

    long long fun(int i, int j, vector<int> &nums, vector<int> &tar)
    {
        vector<int> v;

        for(int k=i; k<=j; k++)
        {
            v.push_back(abs(nums[k]-tar[k]));
        }        

        long long sz=v.size(), cnt=v[0];

        for(int k=1; k<sz; k++)
        {
            if(v[k]>v[k-1])
            {
                cnt+=v[k]-v[k-1];
            }
        }

        return cnt;
    } 

    long long minimumOperations(vector<int>& nums, vector<int>& tar) {

        long  long opr=0;
        int mx=0, flag=-1, ind=0, n=nums.size();

        for(int i=0; i<n; i++)
        {
            if(nums[i]>=tar[i])
            {
                if(flag==1)
                {
                    opr+=fun(ind, i-1, nums, tar);
                    ind=i;
                }

                flag=0;
            }
            else
            {
                if(flag==0)
                {
                    opr+=fun(ind, i-1, nums, tar);
                    ind=i;
                }

                flag=1;
            }
        }

        opr+=fun(ind, n-1, nums, tar);

        return opr;
    }
};
