class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> res(n, 0);
        for(int i = 0; i < n; ++i){
            res[i] = nums[i] - target[i];
        }
        int l = 0, r = 1, s = 0;
        while(r <= n){
            for(; r < n; ++r){
                if((long long)res[r - 1] * (long long)res[r] <= 0) break;
                else continue;
            }
            // cout << r << endl;
            int c = 0;
            int sl = l, maxidx = l;
            for(; l < n && l < r; l++){
                int x = abs(res[l]);
                if(x > c){
                    maxidx = l;
                    c = x;
                }
            }
            // cout << maxidx << " " << n << endl;
            for(int i = sl + 1; i < n && i < maxidx; i++){
                if(abs(res[i - 1]) - abs(res[i]) > 0) ans += abs(res[i - 1] - res[i]);
            }
            for(int i = maxidx + 1;i < n && i < l; i++){
                if(abs(res[i - 1]) - abs(res[i]) < 0) ans += abs(res[i - 1] - res[i]);
            }
            r++;
            ans += c;
        }
        return ans;
    }
};
