typedef long long ll;

class Solution {
public:
    ll fun(vector<int> &arr,int l,int r){
        if(l>r) return 0;
        if(l==r) return arr[l];
        ll minn=LLONG_MAX;
        for(int i=l;i<=r;i++){
            minn=min(minn,(ll)arr[i]);
        }
        ll ans=minn;
        int l1=l;
        for(int i=l;i<=r;i++){
            arr[i]-=minn;
            if(arr[i]==0){
                ans+=fun(arr,l1,i-1);
                l1=i+1;
            }
        }
        ans+=fun(arr,l1,r);
        return ans;
    }
    int sign(int x){
        if(x==0) return 0;
        if(x>0) return 1;
        return -1;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans=0;
        vector<int> curr;
        for(int i=0;i<nums.size();i++){
            if(target[i]!=nums[i] && (curr.empty() || sign(target[i]-nums[i])==sign(curr.back()))){
                    curr.push_back(target[i]-nums[i]);
            }
            else{
                for(int &i:curr){
                    if(i<0) i=-i;
                }
                if(curr.size()) ans+=fun(curr,0,curr.size()-1);
                if(target[i]!=nums[i])
                    curr={target[i]-nums[i]};
                else
                    curr={};
            }
        }
                for(int &i:curr){
                    if(i<0) i=-i;
                }
        if(curr.size())
            ans+=fun(curr,0,curr.size()-1);
        return ans;
    }
};
