class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        for (int i = 0; i < nums.length; i++) {
            nums[i] -= target[i];
        }
        long diff = 0;
        long ans = 0;
        for (int i = 0; i<nums.length; i++) {
            if (nums[i] == 0) {
                diff = 0;
            } else if (nums[i] > 0 && diff < 0) {
                diff = nums[i];
                ans += Math.abs(diff);
            } else if (nums[i] < 0 && diff > 0) {
                diff = nums[i];
                ans += Math.abs(diff);
            } else {
                if (Math.abs(diff) < Math.abs(nums[i])) {
                    ans += Math.abs(nums[i]) - Math.abs(diff);
                    diff = nums[i];
                } else {
                    //ans += Math.abs(diff) - Math.abs(nums[i]);
                    diff = nums[i];
                }
            }
        }
        return ans;
    }
}
