class Solution 
{
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {
        for(int i=0;i<target.size();i++)    target[i] -= nums[i];
        // for(int i=0;i<target.size();i++)    cout<<target[i]<<" ";
        // cout<<endl;

        long long ans = 0;
        int i = 0, j = 0;
        while(j < nums.size())
        {
            i = j;
            while(j < nums.size() && target[j] < 0)
            {
                target[j] = -target[j];
                j++;
            }

            int ans1 = solve(target, i, j-1);
            // cout<<i<<" "<<j-1<<" "<<ans1<<endl;
            ans += ans1;

            while(j < nums.size() && target[j] == 0)
            {
                j++;
            }
            i = j;

            while(j < nums.size() && target[j] > 0)
            {
                j++;
            }
            ans1 = solve(target, i, j-1);
            // cout<<i<<" "<<j-1<<" "<<ans1<<endl;
            ans += ans1;
        }
        return ans;
    }
    long long solve(vector<int>& v, int i, int j)
    {

        if(i > j)   return 0;

        int min_ele = get_min_element(v, i, j);
        // cout<<i<<" "<<j<<" "<<min_ele<<endl;
        long long ans = 0;

        int start = i;
        while(i <= j)
        {
            v[i] -= min_ele;
            if(v[i] == 0)
            {
                if(start != i)  ans += solve(v, start, i-1);
                start = ++i;
            }
            else    i++;
        }
        if(i != start)  ans += solve(v, start, i-1);

        // cout<<i<<" "<<j<<" "<<ans+min_ele<<endl;
        return ans + min_ele;
    }
    int get_min_element(vector<int>& v, int i, int j)
    {
        int ans = INT_MAX;
        while(i <= j)
        {
            ans = min(ans, v[i++]);
        }
        return ans;
    }
};
