class Solution {
public:
    long long solve(vector<int>& n, vector<int>& t)
     {
        int len=n.size();

        vector<int> v(n);

        for (int i = 0; i < len; i++) 
        {
            v[i] = t[i] - n[i];
        }

        long long result = 0;

        int curr = 0;

        for (int i = 0; i < len; i++)
        {
            if (i == 0 || (v[i - 1] > 0 && v[i] > 0) || (v[i - 1] < 0 && v[i] < 0)) 
            {
                int temp =abs(v[i]) - abs(curr);

                if (temp > 0) 
                {
                    result += temp;
                }

                curr = v[i];

            }
            else 
            {
                result += abs(v[i]);

                curr = v[i];
            }
        }

        return result;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {
        long long ans=solve(nums,target);

        return ans;
    }
};
