class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int total = 0;
        int prev = target[0] - nums[0];
        total += Math.abs(prev);
        int n=nums.length;
        int arr[]=new int[n];
        for(int i=0;i<n;i++) arr[i]=target[i]-nums[i];
        int flag=0;
        long a=0;
        long ans=0;
        ans=Math.abs(arr[0]);
        a=ans;
        if(arr[0]>=0) flag=1;
        for (int i = 1; i < nums.length; i++) {
            int curr = target[i] - nums[i];
            if (curr == prev) continue;
            if ((curr > 0 && prev < 0) || (curr < 0 && prev > 0)) {
                total += Math.abs(curr);
                prev = curr;
                continue;
            }
            if (curr < 0 && prev < 0) {
                if (curr > prev) continue;  
                total += Math.abs(curr - prev);
                prev = curr;
                continue;
            }
            if (curr > 0 && prev > 0) {
                if (curr < prev) continue;  
                total += curr - prev;
                prev = curr;
                continue;
            }
        }


        for(int i=1;i<n;i++){
            if(arr[i]<0&&flag==1){
                ans+=Math.abs(arr[i]);
                a=Math.abs(arr[i]);
                flag=0;
                continue;
            }
            if(arr[i]>=0&&flag==0){
                ans+=Math.abs(arr[i]);
                a=Math.abs(arr[i]);
                flag=1;
                continue;
            }
            if(a>=Math.abs(arr[i])){
                a=Math.abs(arr[i]);
                continue;
            }
            else{
                ans+=Math.abs(arr[i])-a;
                a=Math.abs(arr[i]);
            }
        }
        return ans;
    }
}
